import {React , useEffect} from 'react'
import './Header.css'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { NavLink } from 'react-router-dom';
import Anits from '../../assests/banner4.jpg';

const Header = () => {
    useEffect(()=>{
        const navbar = document.querySelector('.navbar');
        const bannerImage =  document.querySelector('.banner-image');

        const handleScroll = () =>{
            const bannerBottom = bannerImage.getBoundingClientRect().bottom;
            if(window.scrollY > bannerBottom) {
                navbar.classList.add('sticky');
            }else{
                navbar.classList.remove('sticky');
            }
        };
        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    return (
        <>
        <header>
        <div className='banner-image'>
            <img className='header_img' src={Anits} alt=''/>
        </div>
            <Navbar expand="lg" className="nav-bar">
                <Container fluid>
                    <Navbar.Brand href="#" className='nav__logo'>IC<span className='nav__logo-part'>DECT 2023</span></Navbar.Brand>
                    <Navbar.Toggle aria-controls="navbarScroll" />
                    <Navbar.Collapse id="navbarScroll">
                        <Nav
                            className="nav__head me-auto my-2 my-lg-0"
                            style={{ maxHeight: '100%' }}
                            navbarScroll
                        >
                            <NavLink className="nav__link nav__align" to="/">Home</NavLink>
                            <NavLink className='nav__link nav__align' to="/institute">About Institute</NavLink>
                            <Nav.Link className='nav__link' href="#conference">About Coference</Nav.Link>
                            <Nav.Link className='nav__link' href="#papers">Call for papers</Nav.Link>
                            <NavLink className='nav__link nav__align' to="/Submission">Submission</NavLink>
                            <NavLink className='nav__link nav__align' to="/Registration">Registration</NavLink>
                            <Nav.Link className='nav__link' href="#dates">Important Dates</Nav.Link>
                            <Nav.Link className='nav__link' href="#team">Conference Team</Nav.Link>
                            <Nav.Link className='nav__link' href="#venue">Venue</Nav.Link>
                            <NavLink className='nav__link nav__align' to="/Gallery">Gallery</NavLink>
                            <Nav.Link className='nav__link' href="#contact">Contact</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            </header>
        </>
    )
}

export default Header
