import React, { useEffect, useState } from 'react';
import './Home.css';
import Message from "../message/Message";
import About_Conference from "../About_conference/About_Conference";
import Callforpapers from "../CallForPapers/Callforpapers";
import Imp_dates from "../ImpDates/Imp_dates";
import Conferenceteam from "../ConferenceTeam/Conferenceteam";
import Welcome from "../Welcome/welcome";
import Contact from '../Contact/contact';
import Venue from '../Venue/venue';

const Home = () => {
    const [isLoaded , setisLoaded] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setisLoaded(true);
        }, 1000); 
        return () => clearTimeout(timer);
    }, []);
    return <>
        <section className='home section' id="home">
            <div className='home__container'>
                <div className='home__content'>
                    <h3 className={`fade-in-text ${isLoaded ? 'visible' : ''}`} id="home__title">7<sup>th</sup> Internation conference  <br /><br />
                     Data Engineering and Communication Technology (ICDECT) 2024</h3>
                </div>
            </div>
        </section>
        <main>
            <Message />
            <About_Conference />
            <Callforpapers />
            <Imp_dates />
            <Conferenceteam />
            <Welcome />
            <Contact />
            <Venue />
        </main>
    </>;
};

export default Home;
