import {React, useState} from 'react'
import './Header.css'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { NavLink } from 'react-router-dom';
import Anits from '../../assests/banner4.jpg';
import {motion} from 'framer-motion';

const variants = {
    visible: {
        x: 0
    },
    hover: {
        scale: 1.1,
        textShadow: '0px 0px 8px rgd(255, 255, 255)',
        transition: {
            duration: 0.3,
            yoyo: Infinity
        }
    }
}

const Header = () => {
    const [isActive , setActive] = useState(false);

    const handleTextClick = () => {
        setActive(true);
    }

    const handleViewTextClick = () =>{
        setActive(false);
    }
    return (
        <>
        <header>
        <div className='banner-image' id="anits">
            <img className='header_img' src={Anits} alt=''/>
        </div>
            <Navbar expand="lg" className="nav-bar">
                <Container fluid>
                    <Navbar.Brand href="#" className='nav__logo'>IC<span className='nav__logo-part'>DECT 2024</span></Navbar.Brand>
                    <Navbar.Toggle className='navToggle' aria-controls="navbarScroll" />
                    <Navbar.Collapse id="navbarScroll">
                        <Nav
                            className="nav__head me-auto my-2 my-lg-0"
                            style={{ maxHeight: '100%' }}
                            navbarScroll
                        >

                            {/* Always display */}
                            <motion.NavLink 
                            variants={variants}
                            animate='visible'
                            whileHover
                            className="nav__link nav__align" to="/" onClick={handleViewTextClick}>Home</motion.NavLink>
                            {/* Display only when Home is active */}
                            {!isActive && <motion.Nav.Link className='nav__link' href="#conference" onClick={handleTextClick}>About Conference</motion.Nav.Link>}
                            {!isActive && <motion.Nav.Link className='nav__link' href="#papers">Call for papers</motion.Nav.Link>}
                            {!isActive && <motion.Nav.Link className='nav__link' href="#dates">Important Dates</motion.Nav.Link>}
                            {!isActive && <motion.Nav.Link className='nav__link' href="#team">Conference Team</motion.Nav.Link>}
                            {/* Always display */}
                            <NavLink className='nav__link nav__align' to="/Gallery" onClick={handleTextClick}>Gallery</NavLink>
                            <NavLink className='nav__link nav__align' to="/institute" onClick={handleTextClick}>About Institute</NavLink>
                            <NavLink className='nav__link nav__align' to="/Submission" onClick={handleTextClick}>Submission</NavLink>
                            <NavLink className='nav__link nav__align' to="/Registration" onClick={handleTextClick}>Registration</NavLink>
                            {/* Display only when Home is active */}
                            {!isActive && <motion.Nav.Link className='nav__link' href="#contact">Contact</motion.Nav.Link>}
                            {!isActive && <motion.Nav.Link className='nav__link' href="#venue">Venue</motion.Nav.Link>}
                            
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            </header>
        </>
    )
}

export default Header
